sys_script_include.script:
function IsValidSysId(strSysId)
{
	strSysId = GetStringValue(strSysId);
	if (strSysId == 'global')
	{
		return true;
	}
	if (strSysId.length == 3)
	{
		var grCurrency = new GlideRecord('fx_currency');
		grCurrency.addQuery('sys_id','=',strSysId);
		grCurrency.query();
		if (grCurrency.next() == true)
		{
			return true;
		}
		else
		{
			return false;
		}
	}
	// The sys_ui_view record for the "Default view" uses the string as a Sys ID
	if (strSysId == 'Default view')
	{
		return true;
	}
	// OOTB sys_app_application "System Import Sets" uses "import_sets" as its Sys ID
	if (strSysId == 'import_sets') 
	{
		return true;
	}	

	// OOTB sys_ui_form on change_request in view "Service Operations Workspace" has a Sys Id that is only 31 characters long
	if (strSysId == 'a23fe35d77c12021dc2c885d9f56481') 
	{
		return true;
	}

	// More anomalies:
	/*

x_snc_devtools (Log): sys_number: 2: "AST"
x_snc_devtools (Log): sys_number: 3: "INC"
x_snc_devtools (Log): sys_number: 4: "TASK"
x_snc_devtools (Log): sys_number: 5: "CHG"
x_snc_devtools (Log): sys_number: 6: "PRB"
x_snc_devtools (Log): sys_number: 9f48a22bc611227b006c74076af1e20: "ADOBE"
x_snc_devtools (Log): sys_number: change_task: "CTASK"
x_snc_devtools (Log): sys_security_type: client_callable_flow_object: "client_callable_flow_object"
x_snc_devtools (Log): sys_security_type: client_callable_script_include: "client_callable_script_include"
x_snc_devtools (Log): sys_security_operation: create: "create"
x_snc_devtools (Log): sys_security_operation: delete: "delete"
x_snc_devtools (Log): sys_number: doc: "DOC"
x_snc_devtools (Log): sys_security_operation: execute: "execute"
x_snc_devtools (Log): sys_mailbox: failed: "Failed"
x_snc_devtools (Log): sys_security_type: GraphQL: "GraphQL"
x_snc_devtools (Log): sys_app_application: import_sets: "System Import Sets"
x_snc_devtools (Log): sys_mailbox: inbox: "Inbox"
x_snc_devtools (Log): sys_mailbox: junk: "Junk"
x_snc_devtools (Log): sys_number: kb_knowledge: "KB"
x_snc_devtools (Log): sys_number: ola: "OLA"
x_snc_devtools (Log): sys_mailbox: outbox: "Outbox"
x_snc_devtools (Log): sys_security_type: pd_action: "pd_action"
x_snc_devtools (Log): sys_security_type: processor: "processor"
x_snc_devtools (Log): sys_security_operation: read: "read"
x_snc_devtools (Log): sys_mailbox: received: "Received"
x_snc_devtools (Log): sys_security_type: record: "record"
x_snc_devtools (Log): sys_number: req2: "REQ"
x_snc_devtools (Log): sys_security_type: REST_Endpoint: "REST_Endpoint"
x_snc_devtools (Log): sys_number: sc_req_item: "RITM"
x_snc_devtools (Log): sys_number: sc_task: "SCTASK"
x_snc_devtools (Log): sys_mailbox: sent: "Sent"
x_snc_devtools (Log): sys_mailbox: skipped: "Skipped"
x_snc_devtools (Log): sys_number: sla: "SLA"
x_snc_devtools (Log): sys_ui_action: sysverb_post_update: "Update"
x_snc_devtools (Log): sys_ui_action: sysverb_query: "Search"
x_snc_devtools (Log): sys_number: sys_import_set: "ISET"
x_snc_devtools (Log): sys_security_type: ui_page: "ui_page"
x_snc_devtools (Log): sys_security_operation: write: "write"


	*/


	if (strSysId.length != 32)
	{
		return false;
	}
	if (StringCheckRegEx(strSysId,'([0-9a-f]){32}') == false)
	{
		return false;
	}
	return true;
}
