sys_script_include.script:
function ConfigurationItemServiceNowAppInsertOrUpdate(
	strInstanceName,
	vApp)
{
	strInstanceName = GetStringValue(strInstanceName);

	var strAppSysId = GetValue(vApp,'sys_id');
	if (IsValidSysId(strAppSysId) == false)
	{
		return false;
	}
	var strAppName = GetValue(vApp,'name');
	if (strAppName == '')
	{
		return false;
	}
	var strAppVersion = GetValue(vApp,'version');
	if (strAppVersion == '')
	{
		return false;
	}	
	var strAppScope = GetValue(vApp,'scope');
	if (strAppScope == '')
	{
		return false;
	}		
	var strAppShortDescription = GetValue(vApp,'short_description');

	var strAppRepoUrl = GetValue(vApp,'x_snc_devtools_repo_url');
	var strAppRepoBranch = GetValue(vApp,'branch');

	return RecordInsertOrUpdate(
			'x_snc_devtools_cmdb_ci_appl_servicenow',
			{
				app_sys_id: strAppSysId,
				app_instance_name: strInstanceName
			},
			{
			},
			{
				app_name: strAppName,
				app_version: strAppVersion,
				app_scope: strAppScope,
				app_short_description: strAppShortDescription,
				app_repo_url: strAppRepoUrl,
				app_repo_branch: strAppRepoBranch
			},
			false);
}
