sys_script_include.script:
function IsCodeRecord(vRecordOrSysId)
{
	if (IsValidSysId(vRecordOrSysId) == true)
	{
		var grMetadata = GetRecord('sys_metadata',vRecordOrSysId);
		if (IsValidRecord(grMetadata) == true)
		{
			return true;
		}
		var grPackage = GetRecord('sys_package',vRecordOrSysId);
		if (IsValidRecord(grPackage) == true)
		{
			return true;
		}
		return false;
	}

	if (IsValidRecord(vRecordOrSysId) == true)
	{
		var strTable = vRecordOrSysId.getRecordClassName();
		if (IsDerivedFromTable(strTable,'sys_metadata') == true)
		{
			return true;
		}
		if (IsDerivedFromTable(strTable,'sys_package') == true)
		{
			return true;
		}

	}
    return false;
}
