sys_script_include.script:
function GetAppRecordsWithCredentialPollution(vApp)
{
	var grApp = GetAppRecord(vApp);
	if (grApp == false)
	{
		return [];
	}
	var aRecords = [];

	var grRecords = new GlideRecord('ldap_server_config');
	grRecords.addQuery('sys_scope','=',grApp.sys_id);
	var condition = grRecords.addQuery('password','!=',null);
	condition.addOrCondition('dn','!=','');
	grRecords.query();
	while (grRecords.next())
	{
		var record =
		{
			sys_id: GetStringValue(grRecords.sys_id),
			sys_class_name: GetStringValue(grRecords.getRecordClassName()),
			name: GetStringValue(grRecords.name)
		};
		aRecords.push(record);
	}

	var grRecords = new GlideRecord('sys_data_source');
	grRecords.addQuery('sys_scope','=',grApp.sys_id);
	var condition = grRecords.addQuery('jdbc_user_name','!=',null);
	condition.addOrCondition('scp_user_name','!=','');
	condition.addOrCondition('jdbc_password','!=','');
	condition.addOrCondition('scp_password','!=','');
	grRecords.query();
	while (grRecords.next())
	{
		var record =
		{
			sys_id: GetStringValue(grRecords.sys_id),
			sys_class_name: GetStringValue(grRecords.getRecordClassName()),
			name: GetStringValue(grRecords.name),
		};
		aRecords.push(record);
	}


	return aRecords;
}
